(function(){var a=window.location;if(!window.XMLHttpRequest){alert('This browser doesnt support XMLHttpRequest so cant send an email');return false}var b=a.protocol+'//'+a.host+a.pathname;var c={key:'your-mandrill-key',message:{html:'New URL bookmark: <a href=\''+b+'\'>'+b+'</a>',text:'New URL bookmark: '+b,subject:'New URL bookmark: '+b,from_email:'your@email.com',from_name:'YourName',to:[{'email':'your@email.com','name':'YourName','type':'to'}],}};var d=new XMLHttpRequest();d.open('POST','https://mandrillapp.com/api/1.0/messages/send.json',true);d.send(JSON.stringify(c));d.onreadystatechange=function(){if(d.readyState==4){if(d.status!=200){alert('Sorry, something went wrong...');return false}else if(d.status==200){alert('Email sent with url: '+b);return true}}}})()